import React, {Component} from 'react';
import {getPerson} from '../actions/index';
import {connect} from 'react-redux';    

const mapStateToProps = state => {
    return {person: state.person}
}

const mapDispatchersToState = {}

class ConnectedCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            person: []
        }
    }

    componentDidMount() {
        this.props.getPerson(); 
    }

    render() {
        return(
            <div className="CardDiv">
                <h3>person card</h3>
            </div>
        );
    }
}

const Card = connect(mapStateToProps, mapDispatchersToState, {getPerson}) (ConnectedCard); 

export default Card;
