import React, {Component} from 'react';
import {getPerson} from '../actions/index';
import {connect} from 'react-redux';    

const mapStateToProps = state => {
    return {person: state.person}
}

class ConnectedCard extends Component {

    // componentDidMount() {
    //     this.props.getPerson(); 
    // }

    render() {
        return(
            <div className="CardDiv">
                <h3>person card</h3> 
                <button onClick={this.props.getPerson}>+</button>
            </div>
        );
    }
}

const Card = connect(mapStateToProps, {getPerson}) (ConnectedCard); 

export default Card;
