import React, {Component} from 'react';
import {getPerson} from '../actions/index';
import {connect} from 'react-redux';    

const mapStateToProps = state => {
    return {person: state.person}
}

class ConnectedCard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            person: []
        }
    }

    render() {
        return(
            <div className="CardDiv">
                <h3>person card</h3>
                <button onClick={this.props.getPerson}>Nuevo esclavo</button>
            </div>
        );
    }
}

const Card = connect(mapStateToProps, {getPerson}) (ConnectedCard); 

export default Card;
