import rootReducer from '../reducer';
import {logger} from '../middleware'; 

import {
    applyMiddleware,
    createStore,
  } from 'redux';
import thunk from 'redux-thunk';
 
const store = createStore(
    rootReducer,
    applyMiddleware(thunk, logger)
    );
 
export default store;
