import {GET_PERSON} from './action-type'; 

export function getPerson () {
    return function (dispatch) {
        return fetch ("https://randomuser.me/api/")
            .then (response => response.json())
            .then ( json => {
                dispatch({type: GET_PERSON, payload: json })
            })
    }
}
