import {GET_PERSON} from '../actions/action-type';      

const initialState = {
    person: [{
        id:      '',
        photo:   '', 
        name:    '', 
        email:   '',
        address: ''
    }]
}

function rootReducer (state = initialState, action) {
    return state; 
    
    if (action.type === GET_PERSON) {
        return {person: state.person.concat({person: action.payload.results[0].name.first})}
        return state; 
    }

}

export default rootReducer;
