import { ADD_MESSAGE } from "../actions/action-type"

const forvidenWords =  [
    "hola",
    "caer",
    "rojo"
]

const banWords = function (store) {
    return function (next) {
        return function (action) {
            console.log("Middleware!")
            console.log(action)
            
            if (action.type === ADD_MESSAGE) {
                let addString = action.payload.title.trim().split(' ').map(banWords);
                console.log(addString); 
            }
        }
    }
}

export {banWords};
