 import {ADD_MESSAGE} from '../actions/action-type';

const initialState = {
    messages: [{title: "Mensaje de prueba", id: 1}]
    // lastId: 1
}

function rootReducer (state = initialState, action) {
    
    if (action.type === ADD_MESSAGE) {
        messages: state.messages.concat ({title: "hola", id: state.lastId + 1})
        // lastId: state.lastId + 1; 
    }

    return state 
}

export default rootReducer;
